entry.value:
Create table if not exists ${hive_output_rates} ( cryptocurrency_name STRING, rate_in_usd DOUBLE, timestamp_date TIMESTAMP ) PARTITIONED BY (year INT, month INT, day INT) clustered by (cryptocurrency_name) into 15 buckets ROW FORMAT DELIMITED FIELDS TERMINATED BY ',' LINES TERMINATED BY '\n' stored as orc;
set hive.exec.dynamic.partition=true; set hive.exec.dynamic.partition.mode=nonstrict; set hive.enforce.bucketing = true;
DROP TABLE IF EXISTS ${hive_temp_table_name};
CREATE TEMPORARY TABLE ${hive_temp_table_name} ( cryptocurrency_name STRING, rate_in_usd DOUBLE, timestamp_date TIMESTAMP, year INT, month INT, day INT) STORED AS orc;
LOAD DATA INPATH 'hdfs://localhost:8020${absolute.hdfs.path}/${filename}' INTO TABLE ${hive_temp_table_name};
INSERT INTO TABLE ${hive_output_rates} PARTITION (year,month,day) SELECT cryptocurrency_name, rate_in_usd, timestamp_date, year, month, day FROM ${hive_temp_table_name};
